import React, { useState } from 'react';
import { Cloud, CloudRain, Sun, CloudSnow, CloudFog, X, Navigation } from 'lucide-react';

const WeatherApp = () => {
  const [cities, setCities] = useState([
    {
      id: 1,
      name: 'Colombo, LK',
      temp: 27,
      condition: 'Few Clouds',
      description: 'Few Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 25,
      tempMax: 28,
      color: 'blue'
    },
    {
      id: 2,
      name: 'Tokyo, JP',
      temp: 7,
      condition: 'Broken Clouds',
      description: 'Broken Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -7,
      tempMax: 7,
      color: 'purple'
    },
    {
      id: 3,
      name: 'Liverpool, GB',
      temp: -2,
      condition: 'Clear Sky',
      description: 'Clear Sky',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -2,
      tempMax: 5,
      color: 'green'
    },
    {
      id: 4,
      name: 'Sydney, AU',
      temp: 26,
      condition: 'Light Rain',
      description: 'Light Rain',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 20,
      tempMax: 30,
      color: 'orange'
    },
    {
      id: 5,
      name: 'Boston, US',
      temp: 13,
      condition: 'Mist',
      description: 'Mist',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 10,
      tempMax: 15,
      color: 'red'
    }
  ]);

  const [cityInput, setCityInput] = useState('');
  const [isSearching, setIsSearching] = useState(false);

  const getWeatherIcon = (condition) => {
    const iconStyle = { width: '2rem', height: '2rem' };
    const lower = condition.toLowerCase();
    if (lower.includes('clear')) return <Sun style={iconStyle} />;
    if (lower.includes('rain')) return <CloudRain style={iconStyle} />;
    if (lower.includes('snow')) return <CloudSnow style={iconStyle} />;
    if (lower.includes('mist') || lower.includes('fog')) return <CloudFog style={iconStyle} />;
    return <Cloud style={iconStyle} />;
  };

  const removeCity = (id) => {
    setCities(cities.filter(city => city.id !== id));
  };

  const addCity = async () => {
    if (!cityInput.trim() || isSearching) return;
    
    setIsSearching(true);
    try {
      const response = await fetch(process.env.NODE_ENV === 'production' 
        ? 'http://localhost:4000/api/weather/search'
        : '/api/weather/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ cityName: cityInput.trim() }),
      });
      
      if (!response.ok) {
        if (response.status === 404) {
          alert('City not found. Please check the spelling and try again.');
        } else {
          throw new Error('Failed to search for city');
        }
        return;
      }
      
      const cityData = await response.json();
      
      // Check if city already exists
      if (cities.find(city => city.id === cityData.id)) {
        alert('This city is already in your dashboard.');
        return;
      }
      
      // Assign a random color for the new city
      const colors = ['blue', 'purple', 'green', 'orange', 'red'];
      const randomColor = colors[Math.floor(Math.random() * colors.length)];
      
      const newCity = {
        ...cityData,
        color: randomColor
      };
      
      setCities(prev => [...prev, newCity]);
      setCityInput('');
      
    } catch (error) {
      console.error('Error adding city:', error);
      alert('Failed to add city. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addCity();
  };

  const getColorClasses = (color) => {
    const colors = {
      blue: 'linear-gradient(to bottom right, #3b82f6, #2563eb)',
      purple: 'linear-gradient(to bottom right, #a855f7, #7c3aed)',
      green: 'linear-gradient(to bottom right, #10b981, #059669)',
      orange: 'linear-gradient(to bottom right, #f97316, #ea580c)',
      red: 'linear-gradient(to bottom right, #ef4444, #dc2626)'
    };
    return colors[color] || colors.blue;
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(to bottom right, #1e3a8a, #1e40af, #3730a3)',
      padding: '2rem'
    }}>
      {/* Header */}
      <div style={{ maxWidth: '80rem', margin: '0 auto', marginBottom: '2rem' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '0.75rem',
          marginBottom: '2rem'
        }}>
          <div style={{ fontSize: '3rem' }}>üå§Ô∏è</div>
          <h1 style={{
            fontSize: '3rem',
            fontWeight: 'bold',
            color: 'white'
          }}>Weather App</h1>
        </div>

        {/* Search Bar */}
        <div style={{
          display: 'flex',
          gap: '0.75rem',
          justifyContent: 'center'
        }}>
          <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '0.75rem' }}>
            <input
              type="text"
              placeholder="Enter city name (e.g., London, Paris, Tokyo)"
              value={cityInput}
              onChange={(e) => setCityInput(e.target.value)}
              style={{
                padding: '0.75rem 1.5rem',
                width: '20rem',
                borderRadius: '0.5rem',
                backgroundColor: 'rgba(55, 65, 81, 1)',
                color: 'white',
                border: 'none',
                outline: 'none'
              }}
              disabled={isSearching}
            />
            <button
              type="submit"
              style={{
                padding: '0.75rem 2rem',
                backgroundColor: '#8b5cf6',
                color: 'white',
                borderRadius: '0.5rem',
                fontWeight: '500',
                border: 'none',
                cursor: isSearching || !cityInput.trim() ? 'not-allowed' : 'pointer',
                opacity: isSearching || !cityInput.trim() ? 0.5 : 1
              }}
              disabled={isSearching || !cityInput.trim()}
            >
              {isSearching ? 'Searching...' : 'Add City'}
            </button>
          </form>
        </div>
      </div>

      {/* Weather Cards Grid */}
      <div style={{
        maxWidth: '80rem',
        margin: '0 auto',
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
        gap: '2rem'
      }}>
        {cities.map(city => (
          <div key={city.id} style={{
            position: 'relative',
            borderRadius: '0.75rem',
            overflow: 'hidden',
            boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)'
          }}>
            {/* Close Button */}
            <button
              onClick={() => removeCity(city.id)}
              style={{
                position: 'absolute',
                top: '1rem',
                right: '1rem',
                zIndex: 10,
                width: '2rem',
                height: '2rem',
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                border: 'none',
                borderRadius: '0.5rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                cursor: 'pointer',
                transition: 'background-color 0.2s'
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.3)'}
              onMouseOut={(e) => e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.2)'}
            >
              <X style={{ width: '1.25rem', height: '1.25rem' }} />
            </button>

            {/* Top Section - Gradient Background */}
            <div style={{
              background: `linear-gradient(to bottom right, ${getColorClasses(city.color)})`,
              padding: '1.5rem',
              position: 'relative',
              overflow: 'hidden'
            }}>
              {/* Decorative circles */}
              <div style={{
                position: 'absolute',
                bottom: '-2.5rem',
                left: '-2.5rem',
                width: '10rem',
                height: '10rem',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                borderRadius: '50%'
              }}></div>
              <div style={{
                position: 'absolute',
                top: '-2.5rem',
                right: '-2.5rem',
                width: '10rem',
                height: '10rem',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                borderRadius: '50%'
              }}></div>

              <div style={{ position: 'relative', zIndex: 10 }}>
                {/* City and Temperature */}
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'start',
                  marginBottom: '1.5rem'
                }}>
                  <div>
                    <h2 style={{
                      fontSize: '1.5rem',
                      fontWeight: 'bold',
                      color: 'white',
                      marginBottom: '0.25rem'
                    }}>{city.name}</h2>
                    <p style={{
                      color: 'rgba(255, 255, 255, 0.8)',
                      fontSize: '0.875rem'
                    }}>9.19am, Feb 8</p>
                  </div>
                  <div style={{
                    fontSize: '4rem',
                    fontWeight: 'bold',
                    color: 'white'
                  }}>
                    {city.temp}¬∞<span style={{ fontSize: '2.5rem' }}>C</span>
                  </div>
                </div>

                {/* Weather Condition */}
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                    <div style={{ color: 'white' }}>
                      {getWeatherIcon(city.condition)}
                    </div>
                    <p style={{
                      color: 'white',
                      fontWeight: '500'
                    }}>{city.description}</p>
                  </div>
                  <div style={{
                    color: 'rgba(255, 255, 255, 0.9)',
                    fontSize: '0.875rem',
                    textAlign: 'right'
                  }}>
                    <p>Temp Min: {city.tempMin}¬∞c</p>
                    <p>Temp Max: {city.tempMax}¬∞c</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Bottom Section - Dark Details */}
            <div style={{
              backgroundColor: 'rgb(55, 65, 81)',
              padding: '1.5rem'
            }}>
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '1rem',
                color: 'rgba(255, 255, 255, 0.9)',
                fontSize: '0.875rem'
              }}>
                {/* Row 1 */}
                <div>
                  <p style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                    Pressure: <span style={{ fontWeight: 'normal' }}>{city.pressure}hPa</span>
                  </p>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <Navigation style={{ width: '1rem', height: '1rem' }} />
                  <p>{city.windSpeed}m/s {city.windDeg} Degree</p>
                </div>
                <div>
                  <p style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                    Sunrise: <span style={{ fontWeight: 'normal' }}>{city.sunrise}</span>
                  </p>
                </div>

                {/* Row 2 */}
                <div>
                  <p style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                    Humidity: <span style={{ fontWeight: 'normal' }}>{city.humidity}%</span>
                  </p>
                </div>
                <div></div>
                <div>
                  <p style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                    Sunset: <span style={{ fontWeight: 'normal' }}>{city.sunset}</span>
                  </p>
                </div>

                {/* Row 3 */}
                <div>
                  <p style={{ fontWeight: '600', marginBottom: '0.25rem' }}>
                    Visibility: <span style={{ fontWeight: 'normal' }}>{city.visibility}km</span>
                  </p>
                </div>
                <div></div>
                <div></div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <div style={{
        textAlign: 'center',
        marginTop: '3rem',
        color: 'rgba(255, 255, 255, 0.6)',
        fontSize: '0.875rem'
      }}>
        2021 Fidenz Technologies
      </div>
    </div>
  );
};

export default WeatherApp;
