import React, { useState } from 'react';
import { Cloud, CloudRain, Sun, CloudSnow, CloudFog, X, Navigation } from 'lucide-react';

const WeatherCard = ({ city, onRemove }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const getWeatherIcon = (description) => {
    const desc = description?.toLowerCase() || '';
    if (desc.includes('rain')) return <CloudRain size={48} />;
    if (desc.includes('snow')) return <CloudSnow size={48} />;
    if (desc.includes('fog') || desc.includes('mist')) return <CloudFog size={48} />;
    if (desc.includes('cloud')) return <Cloud size={48} />;
    return <Sun size={48} />;
  };

  const getCardColor = (temp) => {
    if (temp >= 30) return '#ff6b6b'; // Hot - red
    if (temp >= 20) return '#ffd93d'; // Warm - yellow
    if (temp >= 10) return '#6bcf7f'; // Mild - green
    if (temp >= 0) return '#4ecdc4'; // Cool - teal
    return '#45b7d1'; // Cold - blue
  };

  return (
    <div
      className={`weather-card ${isExpanded ? 'expanded' : ''}`}
      style={{ backgroundColor: getCardColor(city.temp) }}
      onClick={() => setIsExpanded(!isExpanded)}
    >
      <button className="remove-btn" onClick={(e) => { e.stopPropagation(); onRemove(); }}>
        <X size={16} />
      </button>

      <div className="weather-main">
        <h3 className="city-name">{city.name}</h3>
        <div className="temp-display">
          {getWeatherIcon(city.description)}
          <span className="temperature">{Math.round(city.temp)}째C</span>
        </div>
        <p className="condition">{city.description}</p>
      </div>

      {isExpanded && (
        <div className="weather-details">
          <div className="detail-row">
            <span>Pressure:</span>
            <span>{city.pressure || 'N/A'} hPa</span>
          </div>
          <div className="detail-row">
            <span>Humidity:</span>
            <span>{city.humidity || 'N/A'}%</span>
          </div>
          <div className="detail-row">
            <span>Visibility:</span>
            <span>{city.visibility || 'N/A'} km</span>
          </div>
          <div className="detail-row">
            <span>Wind Speed:</span>
            <span>{city.windSpeed || 'N/A'} m/s</span>
          </div>
          <div className="detail-row">
            <span>Wind Direction:</span>
            <span>{city.windDeg || 'N/A'}째</span>
          </div>
          <div className="detail-row">
            <span>Sunrise:</span>
            <span>{city.sunrise || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span>Sunset:</span>
            <span>{city.sunset || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span>Min/Max Temp:</span>
            <span>{city.tempMin || 'N/A'}째 / {city.tempMax || 'N/A'}째</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeatherCard;
