import React, { useState } from 'react';
import { Cloud, CloudRain, Sun, CloudSnow, CloudFog, X } from 'lucide-react';

const WeatherCard = ({ city, onRemove }) => {

  const getWeatherIcon = (condition) => {
    const lower = condition.toLowerCase();
    if (lower.includes('clear')) return <Sun style={{width: '32px', height: '32px'}} />;
    if (lower.includes('rain')) return <CloudRain style={{width: '32px', height: '32px'}} />;
    if (lower.includes('snow')) return <CloudSnow style={{width: '32px', height: '32px'}} />;
    if (lower.includes('mist') || lower.includes('fog')) return <CloudFog style={{width: '32px', height: '32px'}} />;
    return <Cloud style={{width: '32px', height: '32px'}} />;
  };

  const getGradientStyle = (color) => {
    const gradients = {
      blue: 'linear-gradient(to bottom right, #60a5fa, #3b82f6)',
      purple: 'linear-gradient(to bottom right, #a855f7, #9333ea)',
      green: 'linear-gradient(to bottom right, #4ade80, #10b981)',
      orange: 'linear-gradient(to bottom right, #fb923c, #f97316)',
      red: 'linear-gradient(to bottom right, #f87171, #ef4444)'
    };
    return gradients[color] || gradients.blue;
  };

  const cardStyle = {
    position: 'relative',
    borderRadius: '12px',
    overflow: 'hidden',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)'
  };

  const buttonStyle = {
    position: 'absolute',
    top: '16px',
    right: '16px',
    zIndex: 10,
    width: '32px',
    height: '32px',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    borderRadius: '8px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.2s'
  };

  const topSectionStyle = {
    background: getGradientStyle(city.color),
    padding: '24px',
    position: 'relative',
    overflow: 'hidden'
  };

  const decorativeCircle1Style = {
    position: 'absolute',
    bottom: '-40px',
    left: '-40px',
    width: '160px',
    height: '160px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '50%'
  };

  const decorativeCircle2Style = {
    position: 'absolute',
    top: '-40px',
    right: '-40px',
    width: '160px',
    height: '160px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '50%'
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 10
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: '24px'
  };

  const cityNameStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '4px'
  };

  const dateStyle = {
    color: 'rgba(255, 255, 255, 0.8)',
    fontSize: '14px'
  };

  const temperatureStyle = {
    fontSize: '60px',
    fontWeight: 'bold',
    color: 'white'
  };

  const tempUnitStyle = {
    fontSize: '32px'
  };

  const conditionSectionStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  };

  const conditionLeftStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '12px'
  };

  const conditionTextStyle = {
    color: 'white',
    fontWeight: '500'
  };

  const tempRangeStyle = {
    color: 'rgba(255, 255, 255, 0.9)',
    fontSize: '14px',
    textAlign: 'right'
  };

  const bottomSectionStyle = {
    backgroundColor: '#1f2937',
    padding: '24px'
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '16px',
    color: 'rgba(255, 255, 255, 0.9)',
    fontSize: '14px'
  };

  const detailStyle = {
    fontWeight: '600',
    marginBottom: '4px'
  };

  const windSectionStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '8px'
  };

  const windIndicatorStyle = {
    width: '16px',
    height: '16px',
    borderRadius: '50%',
    border: '2px solid rgba(255, 255, 255, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  const windDotStyle = {
    width: '8px',
    height: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.7)',
    borderRadius: '50%'
  };

  return (
    <div style={cardStyle}>
      {/* Close Button */}
      <button
        onClick={onRemove}
        style={buttonStyle}
        onMouseEnter={(e) => e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.3)'}
        onMouseLeave={(e) => e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.2)'}
      >
        <X style={{width: '20px', height: '20px'}} />
      </button>

      {/* Top Section - Gradient Background */}
      <div style={topSectionStyle}>
        {/* Decorative circles */}
        <div style={decorativeCircle1Style}></div>
        <div style={decorativeCircle2Style}></div>

        <div style={contentStyle}>
          {/* City and Temperature */}
          <div style={headerStyle}>
            <div>
              <h2 style={cityNameStyle}>{city.name}</h2>
              <p style={dateStyle}>9.19am, Feb 8</p>
            </div>
            <div style={temperatureStyle}>
              {city.temp}¬∞<span style={tempUnitStyle}>C</span>
            </div>
          </div>

          {/* Weather Condition */}
          <div style={conditionSectionStyle}>
            <div style={conditionLeftStyle}>
              <div style={{color: 'white'}}>
                {getWeatherIcon(city.condition)}
              </div>
              <p style={conditionTextStyle}>{city.description}</p>
            </div>
            <div style={tempRangeStyle}>
              <p>Temp Min: {city.tempMin}¬∞c</p>
              <p>Temp Max: {city.tempMax}¬∞c</p>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Section - Dark Details */}
      <div style={bottomSectionStyle}>
        <div style={gridStyle}>
          {/* Row 1 */}
          <div>
            <p style={detailStyle}>Pressure: <span style={{fontWeight: 'normal'}}>{city.pressure}hPa</span></p>
          </div>
          <div style={windSectionStyle}>
            <div style={windIndicatorStyle}>
              <div style={windDotStyle}></div>
            </div>
            <p>{city.windSpeed}m/s {city.windDeg} Degree</p>
          </div>
          <div>
            <p style={detailStyle}>Sunrise: <span style={{fontWeight: 'normal'}}>{city.sunrise}</span></p>
          </div>

          {/* Row 2 */}
          <div>
            <p style={detailStyle}>Humidity: <span style={{fontWeight: 'normal'}}>{city.humidity}%</span></p>
          </div>
          <div></div>
          <div>
            <p style={detailStyle}>Sunset: <span style={{fontWeight: 'normal'}}>{city.sunset}</span></p>
          </div>

          {/* Row 3 */}
          <div>
            <p style={detailStyle}>Visibility: <span style={{fontWeight: 'normal'}}>{city.visibility}km</span></p>
          </div>
          <div></div>
          <div></div>
        </div>
      </div>
    </div>
  );
};

const WeatherApp = () => {
  const [cities, setCities] = useState([
    {
      id: 1,
      name: 'Colombo, LK',
      temp: 27,
      condition: 'Few Clouds',
      description: 'Few Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 25,
      tempMax: 28,
      color: 'blue'
    },
    {
      id: 2,
      name: 'Tokyo, JP',
      temp: 7,
      condition: 'Broken Clouds',
      description: 'Broken Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -7,
      tempMax: 7,
      color: 'purple'
    },
    {
      id: 3,
      name: 'Liverpool, GB',
      temp: -2,
      condition: 'Clear Sky',
      description: 'Clear Sky',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -2,
      tempMax: 5,
      color: 'green'
    },
    {
      id: 4,
      name: 'Sydney, AU',
      temp: 26,
      condition: 'Light Rain',
      description: 'Light Rain',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 20,
      tempMax: 30,
      color: 'orange'
    },
    {
      id: 5,
      name: 'Boston, US',
      temp: 13,
      condition: 'Mist',
      description: 'Mist',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 10,
      tempMax: 15,
      color: 'red'
    }
  ]);

  const [cityInput, setCityInput] = useState('');

  const getWeatherIcon = (condition) => {
    const lower = condition.toLowerCase();
    if (lower.includes('clear')) return <Sun className="w-8 h-8" />;
    if (lower.includes('rain')) return <CloudRain className="w-8 h-8" />;
    if (lower.includes('snow')) return <CloudSnow className="w-8 h-8" />;
    if (lower.includes('mist') || lower.includes('fog')) return <CloudFog className="w-8 h-8" />;
    return <Cloud className="w-8 h-8" />;
  };

  const removeCity = (id) => {
    setCities(cities.filter(city => city.id !== id));
  };

  const getColorClasses = (color) => {
    const colors = {
      blue: 'from-blue-400 to-blue-500',
      purple: 'from-purple-500 to-purple-600',
      green: 'from-emerald-400 to-emerald-500',
      orange: 'from-orange-400 to-orange-500',
      red: 'from-red-400 to-red-500'
    };
    return colors[color] || colors.blue;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 p-8">
      {/* Header */}
      <div className="max-w-7xl mx-auto mb-8">
        <div className="flex items-center justify-center gap-3 mb-8">
          <div className="text-4xl">üå§Ô∏è</div>
          <h1 className="text-4xl font-bold text-white">Weather App</h1>
        </div>

        {/* Search Bar */}
        <div className="flex gap-3 justify-center">
          <input
            type="text"
            placeholder="Enter a city"
            value={cityInput}
            onChange={(e) => setCityInput(e.target.value)}
            className="px-6 py-3 w-80 rounded-lg bg-gray-800 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
          />
          <button className="px-8 py-3 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-medium transition">
            Add City
          </button>
        </div>
      </div>

      {/* Weather Cards Grid */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8">
        {cities.map(city => (
          <div key={city.id} className="relative rounded-xl overflow-hidden shadow-2xl">
            {/* Close Button */}
            <button
              onClick={() => removeCity(city.id)}
              className="absolute top-4 right-4 z-10 w-8 h-8 bg-white/20 hover:bg-white/30 rounded-lg flex items-center justify-center text-white transition"
            >
              <X className="w-5 h-5" />
            </button>

            {/* Top Section - Gradient Background */}
            <div className={`bg-gradient-to-br ${getColorClasses(city.color)} p-6 relative overflow-hidden`}>
              {/* Decorative circles */}
              <div className="absolute -bottom-10 -left-10 w-40 h-40 bg-white/10 rounded-full"></div>
              <div className="absolute -top-10 -right-10 w-40 h-40 bg-white/10 rounded-full"></div>
              
              <div className="relative z-10">
                {/* City and Temperature */}
                <div className="flex justify-between items-start mb-6">
                  <div>
                    <h2 className="text-2xl font-bold text-white mb-1">{city.name}</h2>
                    <p className="text-white/80 text-sm">9.19am, Feb 8</p>
                  </div>
                  <div className="text-6xl font-bold text-white">
                    {city.temp}¬∞<span className="text-4xl">C</span>
                  </div>
                </div>

                {/* Weather Condition */}
                <div className="flex justify-between items-center">
                  <div className="flex items-center gap-3">
                    <div className="text-white">
                      {getWeatherIcon(city.condition)}
                    </div>
                    <p className="text-white font-medium">{city.description}</p>
                  </div>
                  <div className="text-white/90 text-sm text-right">
                    <p>Temp Min: {city.tempMin}¬∞c</p>
                    <p>Temp Max: {city.tempMax}¬∞c</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Bottom Section - Dark Details */}
            <div className="bg-gray-800 p-6">
              <div className="grid grid-cols-3 gap-4 text-white/90 text-sm">
                {/* Row 1 */}
                <div>
                  <p className="font-semibold mb-1">Pressure: <span className="font-normal">{city.pressure}hPa</span></p>
                </div>
                <div className="flex items-center gap-2">
                  <div className="text-white">
                    <div className="w-4 h-4 rounded-full border-2 border-white/50 flex items-center justify-center">
                      <div className="w-2 h-2 bg-white/70 rounded-full"></div>
                    </div>
                  </div>
                  <p>{city.windSpeed}m/s {city.windDeg} Degree</p>
                </div>
                <div>
                  <p className="font-semibold mb-1">Sunrise: <span className="font-normal">{city.sunrise}</span></p>
                </div>

                {/* Row 2 */}
                <div>
                  <p className="font-semibold mb-1">Humidity: <span className="font-normal">{city.humidity}%</span></p>
                </div>
                <div></div>
                <div>
                  <p className="font-semibold mb-1">Sunset: <span className="font-normal">{city.sunset}</span></p>
                </div>

                {/* Row 3 */}
                <div>
                  <p className="font-semibold mb-1">Visibility: <span className="font-normal">{city.visibility}km</span></p>
                </div>
                <div></div>
                <div></div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
