import React, { useState } from 'react';
import { Cloud, CloudRain, Sun, CloudSnow, CloudFog, X, Navigation } from 'lucide-react';

const WeatherApp = () => {
  const [cities, setCities] = useState([
    {
      id: 1,
      name: 'Colombo, LK',
      temp: 27,
      condition: 'Few Clouds',
      description: 'Few Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 25,
      tempMax: 28,
      color: 'blue'
    },
    {
      id: 2,
      name: 'Tokyo, JP',
      temp: 7,
      condition: 'Broken Clouds',
      description: 'Broken Clouds',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -7,
      tempMax: 7,
      color: 'purple'
    },
    {
      id: 3,
      name: 'Liverpool, GB',
      temp: -2,
      condition: 'Clear Sky',
      description: 'Clear Sky',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: -2,
      tempMax: 5,
      color: 'green'
    },
    {
      id: 4,
      name: 'Sydney, AU',
      temp: 26,
      condition: 'Light Rain',
      description: 'Light Rain',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 20,
      tempMax: 30,
      color: 'orange'
    },
    {
      id: 5,
      name: 'Boston, US',
      temp: 13,
      condition: 'Mist',
      description: 'Mist',
      pressure: '1018',
      humidity: '78',
      visibility: '8.0',
      windSpeed: '4.0',
      windDeg: '120',
      sunrise: '6:05am',
      sunset: '6:05am',
      tempMin: 10,
      tempMax: 15,
      color: 'red'
    }
  ]);

  const [cityInput, setCityInput] = useState('');
  const [isSearching, setIsSearching] = useState(false);

  const getWeatherIcon = (condition) => {
    const lower = condition.toLowerCase();
    if (lower.includes('clear')) return <Sun className="w-8 h-8" />;
    if (lower.includes('rain')) return <CloudRain className="w-8 h-8" />;
    if (lower.includes('snow')) return <CloudSnow className="w-8 h-8" />;
    if (lower.includes('mist') || lower.includes('fog')) return <CloudFog className="w-8 h-8" />;
    return <Cloud className="w-8 h-8" />;
  };

  const removeCity = (id) => {
    setCities(cities.filter(city => city.id !== id));
  };

  const addCity = async () => {
    if (!cityInput.trim() || isSearching) return;
    
    setIsSearching(true);
    try {
      const response = await fetch('/api/weather/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ cityName: cityInput.trim() }),
      });
      
      if (!response.ok) {
        if (response.status === 404) {
          alert('City not found. Please check the spelling and try again.');
        } else {
          throw new Error('Failed to search for city');
        }
        return;
      }
      
      const cityData = await response.json();
      
      // Check if city already exists
      if (cities.find(city => city.id === cityData.id)) {
        alert('This city is already in your dashboard.');
        return;
      }
      
      // Assign a random color for the new city
      const colors = ['blue', 'purple', 'green', 'orange', 'red'];
      const randomColor = colors[Math.floor(Math.random() * colors.length)];
      
      const newCity = {
        ...cityData,
        color: randomColor
      };
      
      setCities(prev => [...prev, newCity]);
      setCityInput('');
      
    } catch (error) {
      console.error('Error adding city:', error);
      alert('Failed to add city. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addCity();
  };

  const getColorClasses = (color) => {
    const colors = {
      blue: 'from-blue-400 to-blue-500',
      purple: 'from-purple-500 to-purple-600',
      green: 'from-emerald-400 to-emerald-500',
      orange: 'from-orange-400 to-orange-500',
      red: 'from-red-400 to-red-500'
    };
    return colors[color] || colors.blue;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 p-8">
      {/* Header */}
      <div className="max-w-7xl mx-auto mb-8">
        <div className="flex items-center justify-center gap-3 mb-8">
          <div className="text-4xl">üå§Ô∏è</div>
          <h1 className="text-4xl font-bold text-white">Weather App</h1>
        </div>

        {/* Search Bar */}
        <div className="flex gap-3 justify-center">
          <form onSubmit={handleSubmit} className="flex gap-3">
            <input
              type="text"
              placeholder="Enter city name (e.g., London, Paris, Tokyo)"
              value={cityInput}
              onChange={(e) => setCityInput(e.target.value)}
              className="px-6 py-3 w-80 rounded-lg bg-gray-800 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
              disabled={isSearching}
            />
            <button 
              type="submit"
              className="px-8 py-3 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-medium transition disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={isSearching || !cityInput.trim()}
            >
              {isSearching ? 'Searching...' : 'Add City'}
            </button>
          </form>
        </div>
      </div>

      {/* Weather Cards Grid */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8">
        {cities.map(city => (
          <div key={city.id} className="relative rounded-xl overflow-hidden shadow-2xl">
            {/* Close Button */}
            <button
              onClick={() => removeCity(city.id)}
              className="absolute top-4 right-4 z-10 w-8 h-8 bg-white/20 hover:bg-white/30 rounded-lg flex items-center justify-center text-white transition"
            >
              <X className="w-5 h-5" />
            </button>

            {/* Top Section - Gradient Background */}
            <div className={`bg-gradient-to-br ${getColorClasses(city.color)} p-6 relative overflow-hidden`}>
              {/* Decorative circles */}
              <div className="absolute -bottom-10 -left-10 w-40 h-40 bg-white/10 rounded-full"></div>
              <div className="absolute -top-10 -right-10 w-40 h-40 bg-white/10 rounded-full"></div>
              
              <div className="relative z-10">
                {/* City and Temperature */}
                <div className="flex justify-between items-start mb-6">
                  <div>
                    <h2 className="text-2xl font-bold text-white mb-1">{city.name}</h2>
                    <p className="text-white/80 text-sm">9.19am, Feb 8</p>
                  </div>
                  <div className="text-6xl font-bold text-white">
                    {city.temp}¬∞<span className="text-4xl">C</span>
                  </div>
                </div>

                {/* Weather Condition */}
                <div className="flex justify-between items-center">
                  <div className="flex items-center gap-3">
                    <div className="text-white">
                      {getWeatherIcon(city.condition)}
                    </div>
                    <p className="text-white font-medium">{city.description}</p>
                  </div>
                  <div className="text-white/90 text-sm text-right">
                    <p>Temp Min: {city.tempMin}¬∞c</p>
                    <p>Temp Max: {city.tempMax}¬∞c</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Bottom Section - Dark Details */}
            <div className="bg-gray-800 p-6">
              <div className="grid grid-cols-3 gap-4 text-white/90 text-sm">
                {/* Row 1 */}
                <div>
                  <p className="font-semibold mb-1">Pressure: <span className="font-normal">{city.pressure}hPa</span></p>
                </div>
                <div className="flex items-center gap-2">
                  <Navigation className="w-4 h-4" />
                  <p>{city.windSpeed}m/s {city.windDeg} Degree</p>
                </div>
                <div>
                  <p className="font-semibold mb-1">Sunrise: <span className="font-normal">{city.sunrise}</span></p>
                </div>

                {/* Row 2 */}
                <div>
                  <p className="font-semibold mb-1">Humidity: <span className="font-normal">{city.humidity}%</span></p>
                </div>
                <div></div>
                <div>
                  <p className="font-semibold mb-1">Sunset: <span className="font-normal">{city.sunset}</span></p>
                </div>

                {/* Row 3 */}
                <div>
                  <p className="font-semibold mb-1">Visibility: <span className="font-normal">{city.visibility}km</span></p>
                </div>
                <div></div>
                <div></div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <div className="text-center mt-12 text-white/60 text-sm">
        2021 Fidenz Technologies
      </div>
    </div>
  );
};

export default WeatherApp;
