import { useState } from "react";

export default function SearchBar({ onAdd }){
  const [value, setValue] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e?.preventDefault();
    if (!value.trim() || loading) return;
    setLoading(true);
    try {
      await onAdd?.(value.trim());
      setValue("");
    } catch (err) {
      // bubble up; App will handle showing errors
      console.error(err);
      alert(err?.message || "Failed to add city");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form className="toolbar" onSubmit={submit}>
      <input
        className="input"
        placeholder="Enter a city (e.g., London)"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        disabled={loading}
      />
      <button className="btn" type="submit" disabled={loading || !value.trim()}>
        {loading ? "Adding..." : "Add City"}
      </button>
    </form>
  );
}
