import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import WeatherGrid from "./components/WeatherGrid";
import WeatherDetail from "./components/WeatherDetail";

export default function App(){
  const [cities, setCities] = useState([]);
  const [selected, setSelected] = useState(null);
  const [err, setErr] = useState("");

  useEffect(() => {
    (async () => {
      try{
        const { data } = await axios.get("/api/weather");
        setCities(data.cities || []);
      }catch(e){
        setErr("Failed to load weather");
      }
    })();
  },[]);

  // Helper to parse short time strings like "12:36 PM" into unix seconds (today)
  const parseTimeToSeconds = (timeStr) => {
    if (!timeStr) return null;
    try{
      // Try to create a Date using today's date + provided time
      const today = new Date();
      const combined = `${today.toDateString()} ${timeStr}`;
      const d = new Date(combined);
      if (isNaN(d.getTime())) return null;
      return Math.floor(d.getTime() / 1000);
    }catch(e){
      return null;
    }
  };

  const handleAddCity = async (cityName) => {
    setErr("");
    try{
      const { data } = await axios.post('/api/weather/search', { cityName });

      // prevent duplicates by id
      if (cities.find(c => String(c.id) === String(data.id))) {
        throw new Error('This city is already in your dashboard.');
      }

      // normalize fields to the shape WeatherCard expects
      const mapped = {
        id: data.id,
        name: (typeof data.name === 'string' && data.name.includes(',')) ? data.name.split(',')[0] : data.name,
        country: data.country || (typeof data.name === 'string' && data.name.split(',')[1]?.trim()) || '',
        dt: Math.floor(Date.now()/1000),
        description: data.description || data.weather?.[0]?.description || "",
        temp: data.temp ?? data.main?.temp ?? null,
        temp_min: data.tempMin ?? data.temp_min ?? data.main?.temp_min ?? null,
        temp_max: data.tempMax ?? data.temp_max ?? data.main?.temp_max ?? null,
        pressure: data.pressure ?? data.main?.pressure ?? null,
        humidity: data.humidity ?? data.main?.humidity ?? null,
        visibility: (function(v){
          // server may return visibility as "10.0" meaning km, or meters
          if (v == null) return null;
          if (typeof v === 'string' && v.includes('.')){
            const km = parseFloat(v);
            if (isNaN(km)) return null;
            return Math.round(km * 1000);
          }
          return v;
        })(data.visibility ?? data.main?.visibility),
        wind_speed: data.windSpeed ?? data.wind?.speed ?? data.wind_speed ?? null,
        wind_deg: data.windDeg ?? data.wind?.deg ?? data.wind_deg ?? null,
        sunrise: parseTimeToSeconds(data.sunrise) ?? null,
        sunset: parseTimeToSeconds(data.sunset) ?? null
      };

      setCities(prev => [...prev, mapped]);
    }catch(e){
      console.error(e);
      setErr(e?.response?.data?.error || e.message || 'Failed to add city');
      throw e;
    }
  };

  return (
    <>
      <div className="bg-clouds" />
      <div className="container">
        <Header />
  {/* Hide SearchBar if you want to strictly follow "no Add City" */}
  <SearchBar onAdd={handleAddCity} />
        {err && <p style={{textAlign:"center", color:"#ffb4b4"}}>{err}</p>}
      </div>

      {selected
        ? <WeatherDetail data={selected} onBack={() => setSelected(null)} />
        : <WeatherGrid list={cities} onOpen={setSelected} />
      }
    </>
  );
}
