import React, { useState } from 'react';
import { 
  formatDate, 
  formatTemperature, 
  getWeatherIcon, 
  getCityColorClass 
} from '../lib/format';
import WeatherDetail from './WeatherDetail';

const WeatherCard = ({ city, onRemove }) => {
  const [showDetails, setShowDetails] = useState(false);

  if (!city) return null;

  const weatherIconClass = getWeatherIcon(city.condition, city.description);
  const cityColorClass = getCityColorClass(city.name);

  const handleCardClick = () => {
    setShowDetails(!showDetails);
  };

  const handleCloseClick = (e) => {
    e.stopPropagation();
    onRemove();
  };

  return (
    <div 
      className={`weather-card ${cityColorClass}`}
      onClick={handleCardClick}
    >
      <button 
        className="close-button"
        onClick={handleCloseClick}
        aria-label="Remove city"
      >
        ×
      </button>

      <div className="card-header">
        <div className="city-info">
          <h2>{city.name || 'Unknown City'}</h2>
          <div className="date">{formatDate()}</div>
        </div>
        <div className="temperature">
          {city.temp !== null ? `${formatTemperature(city.temp)}°C` : '--°C'}
        </div>
      </div>

      <div className="weather-main">
        <div className={`weather-icon ${weatherIconClass}`}></div>
        <div className="weather-description">
          {city.description || city.condition || 'No description'}
        </div>
      </div>

      <div className="temperature-range">
        Temp Min: {city.temp !== null ? `${formatTemperature(city.temp - 2)}°C` : '--°C'}<br />
        Temp Max: {city.temp !== null ? `${formatTemperature(city.temp + 3)}°C` : '--°C'}
      </div>

      {showDetails && <WeatherDetail city={city} />}
    </div>
  );
};

export default WeatherCard;
